import React from "react";
import Auth from "../../utils/auth";
import { Link } from "react-router-dom";
import { QUERY_SEARCH_ITEMS } from "../../utils/queries"; 


function Nav() {

  const [search, setSearch] = useState("");
  const history = useHistory();

  // Set up the useLazyQuery hook for search
  const [searchItems, { data: searchData }] = useLazyQuery(QUERY_SEARCH_ITEMS);

  function handleSearch(event) {
    event.preventDefault();

    // Call the searchItems function with the searchTerm variable
    searchItems({ variables: { searchTerm: search } });

    // Reset the search input
    setSearch("");

    // Navigate to the search results page or update the application state
    history.push("/search-results");
  }

  function showNavigation() {
    if (Auth.loggedIn()) {
      return (
        <ul className="flex-row">
          <li className="mx-1">
            <Link to="/orderHistory">
              Order History
            </Link>
          </li>
          <li className="mx-1">
            {/* this is not using the Link component to logout or user and then refresh the application to the start */}
            <a href="/" onClick={() => Auth.logout()}>
              Logout
            </a>
          </li>
        </ul>
      );
    } else {
      return (
        <ul className="flex-row">
          <li className="mx-1">
            <Link to="/signup">
              Signup
            </Link>
          </li>
          <li className="mx-1">
            <Link to="/login">
              Login
            </Link>
          </li>
        </ul>
      );
    }
  }

  return (
    <header className="flex-row px-1">
      <h1>
        <Link to="/">
      Game Center
        </Link>
      </h1>

      <nav>
        {showNavigation()}
      </nav>
    </header>
  );
}

export default Nav;
